mapper.select:

			SELECT C1.* FROM (
				  SELECT B1.*, ROWNUM RN FROM (
				  SELECT A1.*,count(*) over() as TOTAL  FROM SYS_ROLE A1
                  ORDER BY A1.INSERT_TIME) B1
				  WHERE ROWNUM<=#{endNum}
			) C1
			WHERE RN>#{startNum}

mapper.update:

UPDATE SYS_ROLE SET
ROLE_NAME = #{ROLE_NAME},
DESCPT = #{DESCPT},
UPDATE_TIME = #{UPDATE_TIME}
where ID = #{ID}
